package beans;

public class Nganh {
	private String maNganh;
	private String tenNganh;
	private String loaiNganh;
	
	public Nganh() {
		// TODO Auto-generated constructor stub
	}

	public Nganh(String maNganh, String tenNganh, String loaiNganh) {
		this.maNganh = maNganh;
		this.tenNganh = tenNganh;
		this.loaiNganh = loaiNganh;
	}

	/**
	 * @return the maNganh
	 */
	public String getMaNganh() {
		return maNganh;
	}

	/**
	 * @param maNganh the maNganh to set
	 */
	public void setMaNganh(String maNganh) {
		this.maNganh = maNganh;
	}

	/**
	 * @return the tenNganh
	 */
	public String getTenNganh() {
		return tenNganh;
	}

	/**
	 * @param tenNganh the tenNganh to set
	 */
	public void setTenNganh(String tenNganh) {
		this.tenNganh = tenNganh;
	}

	/**
	 * @return the loaiNganh
	 */
	public String getLoaiNganh() {
		return loaiNganh;
	}

	/**
	 * @param loaiNganh the loaiNganh to set
	 */
	public void setLoaiNganh(String loaiNganh) {
		this.loaiNganh = loaiNganh;
	}

	
	
	
}
